INSERT INTO `v_wc_admin_notes` VALUES("1", "wc-admin-wc-helper-connection", "info", "en_US", "Connect to WooCommerce.com", "Connect to get important product notifications and updates.", "{}", "unactioned", "woocommerce-admin", "2020-11-06 04:37:34", NULL, "0", "plain", "", "0", "info");
INSERT INTO `v_wc_admin_notes` VALUES("2", "wc-admin-onboarding-profiler-reminder", "update", "en_US", "Welcome to WooCommerce! Set up your store and start selling", "We\'re here to help you going through the most important steps to get your store up and running.", "{}", "actioned", "woocommerce-admin", "2020-11-06 04:37:34", NULL, "0", "plain", "", "0", "info");
INSERT INTO `v_wc_admin_notes` VALUES("3", "wc-admin-onboarding-email-marketing", "info", "en_US", "Tips, product updates, and inspiration", "We\'re here for you - get tips, product updates and inspiration straight to your email box", "{}", "unactioned", "woocommerce-admin", "2020-11-06 04:37:34", NULL, "0", "plain", "", "0", "info");
INSERT INTO `v_wc_admin_notes` VALUES("4", "wc-admin-marketing-intro", "info", "en_US", "Connect with your audience", "Grow your customer base and increase your sales with marketing tools built for WooCommerce.", "{}", "unactioned", "woocommerce-admin", "2020-11-06 04:37:34", NULL, "0", "plain", "", "0", "info");
INSERT INTO `v_wc_admin_notes` VALUES("5", "ecomm-need-help-setting-up-your-store", "info", "en_US", "Need help setting up your Store?", "Schedule a free 30-min <a href=\"https://wordpress.com/support/concierge-support/\">quick start session</a> and get help from our specialists. We’re happy to walk through setup steps, show you around the WordPress.com dashboard, troubleshoot any issues you may have, and help you the find the features you need to accomplish your goals for your site.", "{}", "pending", "woocommerce.com", "2020-11-06 04:37:34", NULL, "0", "plain", "", "0", "info");
INSERT INTO `v_wc_admin_notes` VALUES("6", "woocommerce-services", "info", "en_US", "WooCommerce Shipping & Tax", "WooCommerce Shipping &amp; Tax helps get your store “ready to sell” as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.", "{}", "pending", "woocommerce.com", "2020-11-06 04:37:34", NULL, "0", "plain", "", "0", "info");
INSERT INTO `v_wc_admin_notes` VALUES("7", "ecomm-unique-shopping-experience", "info", "en_US", "For a shopping experience as unique as your customers", "Product Add-Ons allow your customers to personalize products while they’re shopping on your online store. No more follow-up email requests—customers get what they want, before they’re done checking out. Learn more about this extension that comes included in your plan.", "{}", "pending", "woocommerce.com", "2020-11-06 04:37:34", NULL, "0", "plain", "", "0", "info");
INSERT INTO `v_wc_admin_notes` VALUES("8", "wc-admin-selling-online-courses", "marketing", "en_US", "Do you want to sell online courses?", "Online courses are a great solution for any business that can teach a new skill. Since courses don’t require physical product development or shipping, they’re affordable, fast to create, and can generate passive income for years to come. In this article, we provide you more information about selling courses using WooCommerce.", "{}", "unactioned", "woocommerce-admin", "2020-11-06 08:01:00", NULL, "0", "plain", "", "0", "info");
INSERT INTO `v_wc_admin_notes` VALUES("9", "mailchimp-for-woocommerce-incomplete-install", "warning", "en_US", "Mailchimp For WooCommerce", "Plugin is not yet connected to a Mailchimp account. To complete the connection, open the settings page.", "{\"getting_started\":true,\"activated\":1604649824,\"activated_formatted\":\"November 6th\"}", "unactioned", "mailchimp-for-woocommerce", "2020-11-06 08:03:44", NULL, "0", "plain", "", "0", "info");
INSERT INTO `v_wc_admin_notes` VALUES("10", "wc-admin-launch-checklist", "info", "en_US", "Ready to launch your store?", "To make sure you never get that sinking \"what did I forget\" feeling, we\'ve put together the essential pre-launch checklist.", "{}", "unactioned", "woocommerce-admin", "2020-11-07 04:46:33", NULL, "0", "plain", "", "0", "info");
INSERT INTO `v_wc_admin_notes` VALUES("11", "wc-admin-mobile-app", "info", "en_US", "Install Woo mobile app", "Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.", "{}", "unactioned", "woocommerce-admin", "2020-11-08 05:29:38", NULL, "0", "plain", "", "0", "info");
INSERT INTO `v_wc_admin_notes` VALUES("12", "wc-admin-usage-tracking-opt-in", "info", "en_US", "Help WooCommerce improve with usage tracking", "Gathering usage data allows us to improve WooCommerce. Your store will be considered as we evaluate new features, judge the quality of an update, or determine if an improvement makes sense. You can always visit the <a href=\"http://cmtdragon.com/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Settings</a> and choose to stop sharing data. <a href=\"https://woocommerce.com/usage-tracking\" target=\"_blank\">Read more</a> about what data we collect.", "{}", "unactioned", "woocommerce-admin", "2020-11-13 05:00:00", NULL, "0", "plain", "", "0", "info");
INSERT INTO `v_wc_admin_notes` VALUES("13", "wc-admin-store-notice-giving-feedback-2", "info", "en_US", "Give feedback", "Now that you’ve chosen us as a partner, our goal is to make sure we\'re providing the right tools to meet your needs. We\'re looking forward to having your feedback on the store setup experience so we can improve it in the future.", "{}", "unactioned", "woocommerce-admin", "2020-11-14 04:45:17", NULL, "0", "plain", "", "0", "info");
INSERT INTO `v_wc_admin_notes` VALUES("14", "wc-admin-insight-first-sale", "survey", "en_US", "Did you know?", "A WooCommerce powered store needs on average 31 days to get the first sale. You\'re on the right track! Do you find this type of insight useful?", "{}", "unactioned", "woocommerce-admin", "2020-11-14 04:45:17", NULL, "0", "plain", "", "0", "info");
